import React, { Children } from 'react';
import './App.css';

function App() {
  return (
    <div>
      <User>안녕하세요. 유저 휘인입니다.</User>
    </div>
  );
}

function User({name = "빛이나는 솔로"}) {
  return <div>안녕하세요. 저는 {name}입니다.</div>
}

User.defaultProps = {
  name : '박휘인',
}




function Child () {

}


function Mother(props) {
  const name = "흥부인";
  return <Child grandFatherName = {props.grandFatherName}/>
}

function GrandFather() {
  // const [value, setValue] = useState (초기값)
  const [name, setName] = useState('할애비는 탕아였습니다.'); // 요거 state
  return <Mother grandFatherName={name} setName={setName} />
}


export default App;
