import React, { useState } from 'react';
import './App.css';



function User(props) {
  return <div className='card-container'>{props.user.age}살 - {props.user.name}</div>
}

function App() {
  // const vegetables = ['감자','고구마','오이','가지','콩나물'];
  const [users, setUsers] = useState([
    { id : 1, age : 30, name : "송중기"},
    { id : 2, age : 24, name : "송강"},
    { id : 3, age : 10, name : "김유정"},
    { id : 4, age : 15, name : "구교환"},
  ]);
  const [name, setName] = useState("")
  const [age, setAge] = useState("")

  const addUserHandler = () => {
    const newUser = {
      id : users.length + 1,
      age : age,
      name, name,
    }

  }

  return(
    <div className='container'>
      {/* <Card name="감자"/>
      <Card name="고구마"/>
      <Card name="오이"/>
      <Card name="가지"/>
      <Card name="콩나물"/> */}

      {/* {vegetables.map((vegetableName) => {
        return (
          <div key={vegetableName}>{vegetableName}</div>
        )
      })} */}

      {users.map((user) => {
        return <User user={user} key={user.id} />
      })}
      <input value={name} placeholder="이름을 입력해주세요." onChange={(e) => {setName(e.target.value)}}></input>
      <input value={age} placeholder="나이를 입력해주세요." onChange={(e) => {setAge(e.target.value)}}></input>
    </div>
  ) 
}

