import './App.css';



function Child() {
  return <div>나는 자식입니다.</div>
}

function App() {

  const clickHandler = () => {
    alert('나는 훌륭하다.')
  }


  return (
  /* <---- HTML/JSX 영역  ---->*/
    <div className="container">
      <p>이것은 내가 만든 App컴포넌트 입니다.</p>
      <button onClick={clickHandler}>Click!</button>
      <Child/>
    </div>
  );
}

export default App;
