import React, { Children, useState } from 'react';
import './App.css';

function App() {
  return <GrandFather/>
}

function Child (props) {
  return (
    <div>
      <button onClick={() => {props.setName("할애비는 탕아")}}>우리할아버지 양반</button>
      <div>{props.grandFatherName}</div>
    </div>
  )
}

function Mother(props) {
  const name = "흥부인";
  return <Child grandFatherName = {props.grandFatherName}/>
}

function GrandFather() {
  // const [value, setValue] = useState (초기값)
  const [name, setName] = useState('할애비는 탕아였습니다.'); // 요거 state
  return <Mother grandFatherName={name} setName={setName} />
}


export default App;
