import React, { useState } from 'react';
import './App.css';



function User(props) {
  return <div>{props.user.age}살 - {props.user.name}</div>
}

function App() {
  // const vegetables = ['감자','고구마','오이','가지','콩나물'];
  const users = [
    { id : 1, age : 30, name : "송중기"},
    { id : 2, age : 24, name : "송강"},
    { id : 3, age : 10, name : "김유정"},
    { id : 4, age : 15, name : "구교환"},
  ]

  return(
    <div className='container'>
      {/* <Card name="감자"/>
      <Card name="고구마"/>
      <Card name="오이"/>
      <Card name="가지"/>
      <Card name="콩나물"/> */}

      {/* {vegetables.map((vegetableName) => {
        return (
          <div key={vegetableName}>{vegetableName}</div>
        )
      })} */}

      {users.map((user) => {
        return (
        <div>
        <User user={user} key={user.id} />
        <User/>
        </div>
        ) 
      })}
    </div>
  ) 
}

//

function Card({name}) {
  // const A = <p>{props.name}</p>

  return (
    <div className='card-container'>
      <div>
        {name}
        {/* {name}으로 부모에 있는 현재 컴포넌트의 name값을 가져옴. << Obj로 값이 표현되기 때문에 가능.   */}
      </div>
    </div>
  )
}

export default App;

